mapper.select:
select
  config_type, config_name, config_value, config_desc, create_date, amend_date
from blog.tbl_config


        and config_type = #{configType}


        and config_name = #{configName}


        and config_value = #{configValue}


        and config_desc = #{configDesc}


        and create_date = #{createDate}


        and amend_date = #{amendDate}
